$dg-theme-colors: () !default;
$dg-theme-colors: map-merge(
    (
        'primary': $dg-primary,
        'primary-light': $dg-primary-light,
        'success': $dg-success,
        'info': $dg-info,
        'warning': $dg-warning,
        'danger': $dg-danger,
        'light': $dg-gray-300,
        'dark': $dg-gray-700,
    ),
    $dg-theme-colors
);

$dg-grid-breakpoints: (
    xs: 0,
    sm: 576px,
    md: 768px,
    lg: 992px,
    xl: 1200px,
) !default;

$dg-spacer: 1rem !default;
$dg-spacers: () !default;
// // stylelint-disable-next-line scss/dollar-variable-default
$dg-spacers: map-merge(
    (
        0: 0,
        1: (
            $dg-spacer * 0.25,
        ),
        2: (
            $dg-spacer * 0.5,
        ),
        3: $dg-spacer,
        4: (
            $dg-spacer * 1.5,
        ),
        5: (
            $dg-spacer * 3,
        ),
    ),
    $dg-spacers
);

