.dg-navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    // height: $dg-navbar-height;
    display: flex;
    align-items: center;
    padding: 0.719rem 1.25rem;
    justify-content: space-between;
    font-size: $dg-font-size-md;
    z-index: 100;
    .nav.nav-spacer {
        display: flex;
        visibility: hidden;
        width: 0;
        margin: 0;
        flex: 0 0 auto;
    }
    .navbar-brand {
        display: inline-flex;
        align-items: center;
        margin-right: 1rem;

        .logo {
            margin-right: 10px;
            width: 30px;
            height: 30px;
        }

        .title {
            font-weight: 600;
            color: $dg-white;
        }
    }

    .navbar-toggler {
        margin-left: 10px;
        display: none;
    }

    .nav {
        display: flex;
        a {
            padding: 6px 18px;
            margin-right: 8px;

            &.active {
                border-radius: 4px;
            }
        }
    }

    .spacer {
        flex: 1;
    }

    .action-items {
        display: flex;
        align-items: center;

        .action-item {
            margin-left: 20px;
        }

        .repo-url {
            .dg-icon {
                font-size: 32px;
            }
        }

        .dg-search-container {
            .search {
                width: 200px;
            }
        }
    }

    @include dg-media-breakpoint-down('md') {
        flex-wrap: wrap;
        justify-content: space-between;

        .navbar-toggler {
            display: block;
        }

        .spacer,
        .nav,
        .action-items {
            display: none;
        }

        .nav {
            flex-direction: column;
            flex-basis: 100%;
            margin-top: 8px;
            margin-bottom: 6px;
        }

        .action-items {
            flex-direction: column;
            align-items: flex-start;
            .action-item {
                margin-bottom: 10px;
            }
        }

        &.show {
            .spacer,
            .nav,
            .action-items {
                display: flex;
            }
        }
    }
}

@mixin dg-navbar-theme($background-color, $box-shadow, $color) {
    background-color: $background-color;
    box-shadow: $box-shadow;
    color: $color;

    .nav {
        a {
            color: $color;
        }
    }
    .navbar-brand {
        .title {
            color: $color;
        }
    }

    .repo-url {
        color: $color;
    }
}

.dg-theme-angular {
    .dg-navbar {
        @include dg-navbar-theme($dg-navbar-background-angular, $dg-navbar-box-shadow-angular, $dg-navbar-color-angular);

        .nav {
            a {
                &:hover {
                    color: rgba($color: $dg-white, $alpha: 0.8); // $gray-200;
                }

                &.active {
                    background: hsla(0, 0%, 100%, 0.15);
                }
            }
        }
    }
}

.dg-theme-default {
    .dg-navbar {
        @include dg-navbar-theme($dg-navbar-background-default, $dg-navbar-box-shadow-default, $dg-navbar-color-default);

        .nav {
            a {
                &:hover {
                    color: $dg-primary;
                }

                &.active {
                    color: $dg-primary;
                    background: rgba($dg-primary, 0.12);
                }
            }
        }
    }
}

.dg-menu {
    overflow-x: hidden;

    .menu-group {
        cursor: pointer;
        .group-header {
            display: flex;
            align-items: center;
            .group-title {
                flex: 1 1 0;
                padding: $dg-sidebar-menu-item-space-y 30px;
                color: $dg-gray-600;
                padding-right: 0;
            }
            .group-arrow {
                margin-right: 30px;
                transition: all 0.5s;
            }
        }
        .group-body {
            margin-left: $dg-sidebar-item-indent;
            max-height: 0;
            opacity: 0;
            overflow: hidden;
            transition: all 0.5s;
        }

        .menu-item {
            margin-left: $dg-sidebar-item-indent;
        }
        &.menu-group--open {
            & > .group-body {
                max-height: 4000px;
                opacity: 1;
            }
            & > .group-header .group-arrow {
                transform: rotate(90deg);
            }
        }
    }

    .menu-item {
        padding: 0px $dg-sidebar-menu-item-space-x/2 0 $dg-sidebar-menu-item-space-x;
        &.active {
            padding-right: $dg-sidebar-menu-item-space-x/2 - 3px;
        }
        display: block;
        // a {
        //     color: $dg-gray-800;
        // }

        &.active {
            border-right: 3px solid $dg-primary;
            .item-content {
                background: rgba($dg-primary, 0.05);
                color: $dg-primary;
            }
            &::after {
                background-color: $dg-primary;
                width: 3px;
                // border-right: 3px solid $dg-primary;
            }
        }

        .item-content {
            display: flex;
            align-items: center;
            margin: 0 -1000px;
            padding: $dg-sidebar-menu-item-space-y 1000px;
            color: $dg-gray-800;
            & > .item-content-title {
                flex: 1 1 0;
            }
            & > .item-content-label {
                flex: 0 0 auto;
                background-color: $dg-white;
                display: flex;
                border-radius: 4px;
            }
        }

        .subtitle {
            color: $dg-gray-600;
            font-size: $dg-font-size-sm;
            margin-left: 4px;
        }

        &:hover {
            .item-content {
                color: $dg-primary;
                text-decoration: none;
            }
        }
    }
}

.dg-sidebar {
    .dg-sidebar-header {
        display: flex;
        flex-direction: column;
        align-items: center;
        padding: 0px 0px 10px 0px;
        border-bottom: 1px solid $dg-gray-210;

        .logo {
            width: 80px;
            height: 80px;
        }

        .title {
            font-weight: 500;
            color: $dg-body-color;
            font-size: $dg-font-size-lg;
            padding: 5px 0px;
        }

        .description {
            color: $dg-gray-700;
            padding: 5px 10px;
        }
    }

    .action-items {
        border-top: 1px solid $dg-gray-210;

        .action-item {
            padding: 10px 30px;
        }
    }

    .dg-search-container {
        margin: 10px;
        .algolia-autocomplete {
            .ds-dropdown-menu {
                min-width: 280px;
            }
        }

        .search {
            width: 240px;
        }
    }
}

.dg-search-container {
    position: relative;

    .search-icon {
        position: absolute;
        top: 50%;
        left: 10px;
        transform: translateY(-50%);
        z-index: 1;
        color: $dg-gray-500;
        pointer-events: none;
        font-size: $dg-font-size-base;
    }

    .search {
        padding: 0.532rem 10px 0.532rem 30px;
        outline: none;
        border: 1px solid $dg-gray-200;
        border-radius: 18px;
        -webkit-appearance: none;
    }

    .search-results-container {
        display: none;
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        margin: 8px 0 0;
        min-width: 200px;
        max-width: 400px;
        max-height: 230px;
        padding: 6px 0;
        z-index: 10;
        background-color: $dg-white;
        border-radius: 1px;
        box-shadow: 0 0 24px rgb(0 0 0 / 18%);
        box-sizing: border-box;
        overflow-y: auto;

        &.is-searching {
            display: block;
        }

        &.result-empty {
            text-align: center;
            padding: 40px 0;
            .empty-icon {
                svg {
                    width: 64px;
                    height: 58px;
                }
            }
        }

        .search-result {
            display: block;
            padding: 6px 20px;
            color: $dg-gray-700;
            font-size: $dg-font-size-base;
            text-decoration: none;
            line-height: $dg-line-height;
            transition: background-color 0.2s;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            cursor: pointer;

            &:hover {
                background-color: $dg-gray-80;
                color: $dg-primary;
            }
            .dg-word-highlight {
                background-color: rgba($dg-warning, 0.4);
            }
        }
    }
}

.dg-footer {
    display: block;
    padding: 20px;
    border-top: 1px solid $dg-gray-200;
    text-align: center;
    color: $dg-gray-600;
    a {
        color: $dg-gray-600;
        text-decoration: none;
        &:hover {
            color: $dg-gray-700;
            text-decoration: none;
        }
    }

    .beian {
        margin-top: 5px;
    }
    .beian-public-security {
        margin-left: 5px;
        img {
            vertical-align: -4px;
        }
    }
}

@mixin top-show-title-variant() {
    position: relative;
    margin-top: 6px;
    &::before {
        position: absolute;
        content: attr(title);
        top: 0;
        left: 1em;
        margin-left: -4px;
        padding: 0 4px;
        color: $dg-gray-700;
        font-weight: 500;
        background-color: $dg-white;
        transform: translateY(-50%);
    }
}

.dg-example-viewer {
    display: block;
    padding: 20px 0px;

    &.dg-example-viewer-inline {
        padding: 10px 0px;
        .dg-example-viewer-wrapper {
            border: none;
            box-shadow: none;
        }

        .dg-example-viewer-header,
        .dg-example-viewer-actions {
            display: none;
        }
        .dg-example-viewer-body {
            padding: 0px;
        }
    }

    .dg-example-viewer-wrapper {
        border: 1px solid $dg-border-color;
        // box-shadow: 0 0 10px -4px rgba(0, 0, 0, 0.1);
    }

    .dg-example-viewer-header {
        border-bottom: 1px solid $dg-border-color;
        padding: 5px 20px;
        background: $dg-gray-100; // rgba(0, 0, 0, 0.03);
        color: $dg-gray-700;

        .title {
            font-size: $dg-font-size-lg;
            font-weight: bold;
        }
    }

    .dg-example-viewer-body {
        padding: 20px;
    }

    .dg-example-viewer-intro {
        border-top: 1px solid $dg-border-color;
        padding: 20px;
        @include top-show-title-variant();
    }

    .dg-example-viewer-actions {
        border-top: 1px dashed $dg-border-color;
        padding: 10px;
        text-align: center;
        // @include top-show-title-variant();
        clear: both;
        .action-item {
            color: $dg-gray-500;
            padding: 5px;
            margin-right: 10px;

            &:hover {
                color: $dg-primary;
                font-weight: 500;
            }
        }
    }

    .dg-example-viewer-sources {
        border-top: 1px solid $dg-border-color;
        display: block;
        flex-direction: column;
        transition: opacity 1s;
        height: 0px;
        opacity: 0;
        visibility: hidden;

        &.dg-sources-show {
            visibility: visible;
            height: auto;
            opacity: 1;
            .dg-source-code,
            .dg-example-viewer-sources-content {
                height: auto;
            }
        }

        .dg-tab-links {
            padding: 0px 10px;
        }
    }
}

.dg-examples {
    .dg-example-viewer {
        margin-bottom: 10px;
    }
}

.dg-locales-selector {
    display: block;
    position: relative;
    margin: 0;
    cursor: pointer;

    select {
        width: 100%;
        padding: 0 22px 0 0;
        text-align: right;
        height: 30px;
        outline: none;
        font-size: 14px;
        appearance: none;
        border: none;
        background: transparent;
        cursor: pointer;
    }

    .dg-selector-arrow {
        width: 9px;
        height: 9px;
        border-left: 1px solid $dg-gray-500;
        border-top: 1px solid $dg-gray-500;
        transform: rotate(-135deg);
        position: absolute;
        top: 50%;
        right: 2px;
        margin-top: -8px;
        right: 2px;
        z-index: -1;
    }
}

@mixin dg-locales-selector-theme($color, $hover-color) {
    .dg-locales-selector {
        select {
            color: $color;
        }
        .dg-selector-arrow {
            border-color: $color;
        }
        &:hover {
            select {
                color: $hover-color;
            }
            .dg-selector-arrow {
                border-color: $hover-color;
            }
        }
    }
}

.dg-theme-default {
    @include dg-locales-selector-theme(inherit, rgba($color: $dg-body-color, $alpha: 0.8));
}

.dg-theme-angular {
    @include dg-locales-selector-theme($dg-white, rgba($color: $dg-white, $alpha: 0.8));
}

.dg-copy {
    cursor: pointer;
}

.dg-source-code {
    display: block;
    padding: 10px 20px;
    background: $dg-gray-100;
    white-space: nowrap;
    margin: 0px;
    height: 0px;
    position: relative;

    .dg-source-code-content {
        white-space: break-spaces;
        padding: 10px 0px;
        font-size: $dg-font-size-md;
    }

    .dg-copy {
        position: absolute;
        top: 1em;
        right: 1.1em;
        color: $dg-gray-500;
        &:hover {
            color: $dg-primary;
        }
    }
}

